		   var generation2 = person2list[i+1];
         var commonAncestor = getCaption(person1list[j]);
		   if (generation1 == "1" && generation2 == "1") {
           if (short == "short") {
              return "sibling";
           }
			  return caption1 + " is a sibling of " + caption2 + ".  In general, 50% of their genes are common.";
		   }

         var level1 = parseInt(generation1);
