/*\
title: chapternotes.js
type: application/javascript
module-type: macro

Macro to return footnote citations to a chapter

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "chapternotes";

exports.params = [
{ name: "chapter" }
];

/*
Run the macro
*/
exports.run = function(chapter) {
   var markers = [];
   var textbody = "";
   var returnString = "";

   var tiddler = $tw.wiki.getTiddler(chapter);
   if (tiddler != null) {
     textbody = tiddler.getFieldString("text");
	 if (textbody == "") {
	     return "Error (4) - Text body of chapter tiddler ( " + chapter + " ) found empty."
	 }
   } else {
      return "Error (3) - Chapter tiddler ( " + chapter + " ) not found."	  
   }
   var startpos = 0;
   var max = 0;
   var position = 1;
   var positionend = -1;
   while (position > 0 && max < 200) {
     var thissearchstring = textbody.substring(startpos);
     position = thissearchstring.search("<<clstip ");
	  if (position > -1 ) {
	     positionend =  thissearchstring.search(">>");
		 if (positionend == -1) {			
			return "Error (1) in usage of clstip near position: " + position;
		 }
		 var clsstring = thissearchstring.substring(position, positionend+2)
		 const cls = clsstring.split("\"");
		 if (cls.length < 8) {
		    return "Error (2) in usage of clstip near position: " + position + " element count: " + cls.length;
		 }
       var bfound = false;
       for (let i=0; i<markers.length; i++) {
           if (cls[3] == markers[i]) {
              bfound = true;
              break;
           }
       }
       if (!bfound) {
          markers.push(cls[3]);
	   	 returnString = returnString + "<div class=\"cit\"><a id=\"#" + cls[3] + "\">" + cls[3] + "</a> {{" + cls[1] + cls[7] + "}}</div><br/>";
          startpos = startpos + positionend + 2;
       }
       max++;
	  }
   }   
   return returnString;
};

})();

/*\
title: encodeHTML.js
type: application/javascript
module-type: macro

Macro to return HTML encoded characters of a string.

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports.name = "encodeHTML";

exports.params = [
{ name: "rawstr" }
];

exports.run = function(rawstr) {

return rawstr.replace(/./gm, function(s) {
  return (s.match(/[a-z0-9\s]+/i)) ? s : "&#" + s.charCodeAt(0) + ";";
  });

};
})();

/*\
title: GenChartData.js
type: application/javascript
module-type: macro

Macro to return informal name format of a name

\*/

(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "GenChartData";

exports.params = [
{ name: "person1name" }
];

function encode(e){return e.replace(/[^]/g,function(e){return"&#"+e.charCodeAt(0)+";"})}

function personExists(person) {
   var tiddler = $tw.wiki.getTiddler(person);
   if (tiddler != null) {
      return true;
   }
   return false;
}

function getCaption(person) {
   var tiddler = $tw.wiki.getTiddler(person);
   if (tiddler != null) {
      var captionValue = tiddler.getFieldString("caption");
      if (captionValue != "") {
         return captionValue;
      }
   }
   return person;
}

function getAncestor(person, generation, personlist) {
   if (generation > 30) {
      return personlistx;
   }
   var tiddler = $tw.wiki.getTiddler(person);
   var personlistx = personlist;
   if (tiddler != null) {
      var fatherValue = $tw.utils.parseStringArray(tiddler.getFieldString("father") || "");
		var motherValue = $tw.utils.parseStringArray(tiddler.getFieldString("mother") || "");

		if (fatherValue.length > 0 || motherValue.length > 0) personlistx = personlistx + ",\"children\": [";

		if (fatherValue.length > 0) {
			var fcaption = getCaption(fatherValue[0]);
			personlistx = personlistx + "{ \"name\": \"" + fcaption + "\"";
			personlistx = getAncestor(fatherValue[0], generation+1, personlistx);
			personlistx = personlistx + "}";
			if (motherValue.length > 0) personlistx = personlistx + ",";
		}
		if (motherValue.length > 0) {
			var mcaption = getCaption(motherValue[0]);
			personlistx = personlistx + "{ \"name\": \"" + mcaption + "\"" ;
			personlistx = getAncestor(motherValue[0], generation+1, personlistx);
			personlistx = personlistx + "}";
		}	
		if (fatherValue.length > 0 || motherValue.length > 0) {
			personlistx = personlistx + "]";
		} else {
			personlistx = personlistx + ", \"size\": 1";
		}
	} else {
      personlistx = personlistx + ", \"size\": 1";
   }
	return personlistx;
}

/*
Run the macro
*/
exports.run = function(person1name) {

var p1 = $tw.utils.parseStringArray(person1name || "");
var per1 = p1[0];

if (!personExists(per1)) {
   return "individual does not exist.";
}

var caption1 = getCaption(per1);

var safestring = "<head><style> body { font-family: Sans-serif; font-size: 11px; } .slice { cursor: pointer; } .slice .main-arc { stroke: #fff; stroke-width: 1px; } .slice .hidden-arc { fill: none; } .slice text { pointer-events: none; dominant-baseline: middle; text-anchor: middle; } </style> <script src='https://d3js.org/d3.v4.min.js'></script></head><body> <script>"

var estring = encode(safestring);

var stringlist = estring + "const width = window.innerWidth, height = window.innerHeight, maxRadius = (Math.min(width, height) / 2) - 5; const formatNumber = d3.format(',d'); const x = d3.scaleLinear() .range([0, 2 * Math.PI]) .clamp(true); const y = d3.scaleSqrt() .range([maxRadius*.1, maxRadius]); const color = d3.scaleOrdinal(d3.schemeCategory20); const partition = d3.partition(); const arc = d3.arc() .startAngle(d => x(d.x0)) .endAngle(d => x(d.x1)) .innerRadius(d => Math.max(0, y(d.y0))) .outerRadius(d => Math.max(0, y(d.y1))); const middleArcLine = d => { const halfPi = Math.PI/2; const angles = [x(d.x0) - halfPi, x(d.x1) - halfPi]; const r = Math.max(0, (y(d.y0) + y(d.y1)) / 2); const middleAngle = (angles[1] + angles[0]) / 2; const invertDirection = middleAngle > 0 && middleAngle < Math.PI; if (invertDirection) { angles.reverse(); } const path = d3.path(); path.arc(0, 0, r, angles[0], angles[1], invertDirection); return path.toString(); }; const textFits = d => { const CHAR_SPACE = 6; const deltaAngle = x(d.x1) - x(d.x0); const r = Math.max(0, (y(d.y0) + y(d.y1)) / 2); const perimeter = r * deltaAngle; return d.data.name.length * CHAR_SPACE < perimeter; }; const svg = d3.select('body').append('svg') .style('width', '100vw') .style('height', '100vh') .attr('viewBox', &#96;${-width / 2} ${-height / 2} ${width} ${height}&#96;) .on('click', () => focusOn());var json = {	\"name\": \"" + caption1 + "\"";

var list1 = getAncestor(per1, 1, stringlist);

var safestring2 = "</script></body>";
var estring2 = encode(safestring2);

return list1 + "};root = json; root = d3.hierarchy(root); root.sum(d => d.size); const slice = svg.selectAll('g.slice') .data(partition(root).descendants()); slice.exit().remove(); const newSlice = slice.enter() .append('g').attr('class', 'slice') .on('click', d => { d3.event.stopPropagation(); focusOn(d); }); newSlice.append('title') .text(d => d.data.name + '\\n' + formatNumber(d.value)); newSlice.append('path') .attr('class', 'main-arc') .style('fill', d => color((d.children ? d : d.parent).data.name)) .attr('d', arc); newSlice.append('path') .attr('class', 'hidden-arc') .attr('id', (_, i) => &#96;hiddenArc${i}&#96;) .attr('d', middleArcLine); const text = newSlice.append('text') .attr('display', d => textFits(d) ? null : 'none'); text.append('textPath') .attr('startOffset','50%') .attr('xlink:href', (_, i) => &#96;#hiddenArc${i}&#96; ) .text(d => d.data.name); function focusOn(d = { x0: 0, x1: 1, y0: 0, y1: 1 }) { const transition = svg.transition() .duration(750) .tween('scale', () => { const xd = d3.interpolate(x.domain(), [d.x0, d.x1]), yd = d3.interpolate(y.domain(), [d.y0, 1]); return t => { x.domain(xd(t)); y.domain(yd(t)); }; }); transition.selectAll('path.main-arc') .attrTween('d', d => () => arc(d)); transition.selectAll('path.hidden-arc') .attrTween('d', d => () => middleArcLine(d)); transition.selectAll('text') .attrTween('display', d => () => textFits(d) ? null : 'none'); moveStackToFront(d); function moveStackToFront(elD) { svg.selectAll('.slice').filter(d => d === elD) .each(function(d) { this.parentNode.appendChild(this); if (d.parent) { moveStackToFront(d.parent); } }) } }" + estring2;

};

})();

/*\
title: getAge.js
type: application/javascript
module-type: macro

Macro to return the age of an individual

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "getAge";

exports.params = [
{ name: "birth" }, { name: "death"}
];

/*
Run the macro
*/
exports.run = function(birth, death) {

var wbirth=birth;
var wdeath=death;
var area=" starting...";

if (birth.length == 4 || death.length == 4) {
    wbirth=birth.substring(0,4) + "-01-01";
    wdeath=death.substring(0,4) + "-01-01";
    area=" only4";
} else {
    if (birth.length == 7 || death.length == 7) { 
        wbirth=birth.substring(0,7) + "-01";
        wdeath=death.substring(0,7) + "-01";
        area=" only7";
    }
}

if (wbirth.length == 10 && wdeath.length == 10) {
    var DOB = new Date(wbirth);
    DOB = new Date( DOB.getTime() - DOB.getTimezoneOffset() * -60000 );
    var DOD = new Date(wdeath);
    DOD = new Date( DOD.getTime() - DOD.getTimezoneOffset() * -60000 );
    var totalMonths = (DOD.getFullYear() - DOB.getFullYear()) * 12 + DOD.getMonth() - DOB.getMonth();
    totalMonths += DOD.getDate() > DOB.getDate() ? -1 : 0;
    var years = DOD.getFullYear() - DOB.getFullYear();
    area=" 10";
    if (DOB.getMonth() > DOD.getMonth()) {
        years = years - 1;
        totalMonths = totalMonths - 1;
	 }
    else if (DOB.getMonth() == DOD.getMonth()) {
        if (DOB.getDate() > DOD.getDate()) {
		    years = years - 1;
			 totalMonths = totalMonths - 1;
		  }
	 }
    var days;
    var months;
    if (DOB.getDate() > DOD.getDate()) {
        months = (totalMonths % 12);
        if (months == 0)
            months = 11;
        var x = DOD.getMonth();
        switch (x) {
            case 0:
            case 2:
            case 4:
            case 6:
            case 7:
            case 9:
            case 11: {
                var a = DOB.getDate() - DOD.getDate();
                days = 31 - a;
                break;
            }
            case 1: {
                var a = DOB.getDate() - DOD.getDate();
                days = 28 - a;
                break;
            }
            default: {
                var a = DOB.getDate() - DOD.getDate();
                days = 30 - a;
                break;
            }
        }
        area = " complex ";
    }
    else {
        days = DOD.getDate() - DOB.getDate();
        months = DOD.getMonth() - DOB.getMonth();
        if (months < 0 ) {
            months = months + 12;
        }
        area=" simple";
    }
    var age = years + ' years ' + months + ' months ' + days + ' days';
    if (birth.length == 4 || death.length == 4) {
        age = years + ' years';
    } else {
        if (birth.length == 7 || death.length == 7) { 
            age = years + ' years ' + months + ' months';
        }
    }
    return age;
  } else {
    return "";
  }
};

})();

/*\
title: getName.js
type: application/javascript
module-type: macro

Macro to return informal name format of a name

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "getName";

exports.params = [
{ name: "caption" }, {name: "title"}
];

/*
Run the macro
*/
exports.run = function(caption, title) {

if (caption.length > 0) {
  var n = caption.indexOf(" (");
  if (n == -1) {
    return caption;
  }
  return caption.substr(0, n);
} else {
    return title;
}

};

})();

/* \
title: getPersonScore.js
type: application/javascript
module-type: macro

Macro to return informal name format of a name

\ */

(function () {
/* jslint node: true, browser: true */
  /* global $tw: false */

  /*
Information about this macro
*/

  exports.name = 'getPersonScore';

  exports.params = [
    { name: 'personname' },
  ];

  function personExists(person) {
    const tiddler = $tw.wiki.getTiddler(person);
    if (tiddler != null) {
      return true;
    }
    return false;
  }

  function tagExist(tiddler, tag) {
    if (tiddler.fields.tags != null) {
	   if (tiddler.fields.tags.indexOf(tag) !== -1) {
		  return true;
	   } 
    }
    return false;
  }

  function getCaption(person) {
    const tiddler = $tw.wiki.getTiddler(person);
    if (tiddler != null) {
      const captionValue = tiddler.getFieldString('caption');
      if (captionValue != '') {
        return captionValue;
      }
    }
    return person;
  }

  function getAssertionScore(tiddlerTitle) {
    const tiddler = $tw.wiki.getTiddler(tiddlerTitle);
    var score = 0;
    if (tiddler != null) {
      if (tiddler.fields["proof"] !== null) {
         if (tiddler.fields["proof"] == "argument") score=score+1;
         if (tiddler.fields["proof"] == "statement") score=score+2;
         if (tiddler.fields["proof"] == "summary") score=score+2;
      }
      const evidencetiddlers = $tw.wiki.filterTiddlers(tiddler.fields.evidence, null, '');
      $tw.utils.each(evidencetiddlers, (title)	 => {
        const evidencetiddler = $tw.wiki.getTiddler(title);
        if (evidencetiddler != null) {
           score++;
           if (evidencetiddler.fields["strength"] !== null) {
              if (evidencetiddler.fields["strength"] == "strong") score=score+2;
              if (evidencetiddler.fields["strength"] == "moderate") score=score+1;
              if (evidencetiddler.fields["strength"] == "weak") score=score-1;
           }
           if (evidencetiddler.fields["infovalue"] !== null) {
              if (evidencetiddler.fields["infovalue"] == "none") score=score-1;
              if (evidencetiddler.fields["infovalue"] == "incomplete") score=score-1;
           }
           if (evidencetiddler.fields["conflictvalue"] !== null) {
              if (evidencetiddler.fields["conflictvalue"] == "unknown") score=score-1;
              if (evidencetiddler.fields["conflictvalue"] == "descrepancy") score=score-2;
              if (evidencetiddler.fields["conflictvalue"] == "contradicting") score=score-2;
           }
           const tiddlers = $tw.wiki.filterTiddlers(evidencetiddler.fields.source, null, '');
           $tw.utils.each(tiddlers, (title)	 => {
			   const sourcetiddler = $tw.wiki.getTiddler(title);
			   if (sourcetiddler != null) {
				  score++;
				  if (sourcetiddler.fields["type-of-information"] !== null) {
					 if (sourcetiddler.fields["type-of-information"] == "Primary") score=score+4;
					 if (sourcetiddler.fields["type-of-information"] == "Secondary") score=score+2;
				  }
			   }
		    });
        }
      });
    }
    return score;
  }

  function getEventScore(person) {
    const tiddler = $tw.wiki.getTiddler(person);
    var score = 0;
    if (tiddler != null) {
      const tiddlers = $tw.wiki.filterTiddlers("[tag[event]tag[birth]contains:people[" + tiddler.fields.title + "]]", null, '');
      $tw.utils.each(tiddlers, (title)	 => {
        const eventtiddler = $tw.wiki.getTiddler(title);
        score++;
		  if (eventtiddler != null) {
          if (eventtiddler.fields.date != null) {
              if (eventtiddler.fields.date.length == 9) score++;
          }

			const assertiontiddlers = $tw.wiki.filterTiddlers("[tag[assertion]contains:event[" + eventtiddler.fields.title + "]]", null, '');
			$tw.utils.each(assertiontiddlers, (sourcetitle) => {
			  score += getAssertionScore(sourcetitle);
			});
		  }
      });
      const dtiddlers = $tw.wiki.filterTiddlers("[tag[event]tag[death]contains:people[" + tiddler.fields.title + "]]", null, '');
      $tw.utils.each(dtiddlers, (title)	 => {
        const eventtiddler = $tw.wiki.getTiddler(title);
        score++;
        if (eventtiddler != null) {
          if (eventtiddler.fields.date != null) {
              if (eventtiddler.fields.date.length == 9) score++;
          }
          if (eventtiddler.fields.causeofdeath != null) {
              if (eventtiddler.fields.causeofdeath.length > 0) score=score+1;
          }
        } 
		if (eventtiddler != null) {
			const assertiontiddlers = $tw.wiki.filterTiddlers("[tag[assertion]contains:event[" + eventtiddler.fields.title + "]]", null, '');
			$tw.utils.each(assertiontiddlers, (sourcetitle) => {
			  score += getAssertionScore(sourcetitle);
			});
		}
      });

      const mtiddlers = $tw.wiki.filterTiddlers("[tag[event]tag[marriage]contains:people[" + tiddler.fields.title + "]]", null, '');
      $tw.utils.each(mtiddlers, (title)	 => {
        const eventtiddler = $tw.wiki.getTiddler(title);
        score++;
        if (eventtiddler != null) {
          if (eventtiddler.fields.date != null) {
              if (eventtiddler.fields.date.length == 9) score++;
          }
         }
		if (eventtiddler != null) {
			const assertiontiddlers = $tw.wiki.filterTiddlers("[tag[assertion]contains:event[" + eventtiddler.fields.title + "]]", null, '');
			$tw.utils.each(assertiontiddlers, (sourcetitle) => {
			  score += getAssertionScore(sourcetitle);
			});
		}
      });

      const bbtiddlers = $tw.wiki.filterTiddlers("[tag[event]tag[burial]contains:people[" + tiddler.fields.title + "]]", null, '');
      $tw.utils.each(bbtiddlers, (title)	 => {
        const eventtiddler = $tw.wiki.getTiddler(title);
        score++;
        if (eventtiddler != null) {
        if (eventtiddler.fields.date != null) {
              if (eventtiddler.fields.date.length == 9) score++;
          }
         }
		if (eventtiddler != null) {
			const assertiontiddlers = $tw.wiki.filterTiddlers("[tag[assertion]contains:event[" + eventtiddler.fields.title + "]]", null, '');
			$tw.utils.each(assertiontiddlers, (sourcetitle) => {
			  score += getAssertionScore(sourcetitle);
			});
		}
      });
    }
    return score;
  }

  /*
Run the macro
*/
  exports.run = function (personname) {
    var score = 0;
    if (personExists(personname)) {
      const tiddler = $tw.wiki.getTiddler(personname);
	   if (tiddler.fields.mother != null) {
        if (tiddler.fields.mother.length > 0) {
           score++;
        }
      }
      if (tiddler.fields.father != null) {
        if (tiddler.fields.father.length > 0) {
           score++;
        }
      }
      if (tiddler.fields.spouse != null) {
        if (tiddler.fields.spouse.length > 0) {
           score++;
        }
      }
      if (tiddler.fields.occupations != null) {
        if (tiddler.fields.occupations.length > 0) {
           score++;
        }
      }
      if (tiddler.fields.aka != null) {
        if (tiddler.fields.aka.length > 0) {
           score++;
        }
      }
      if (tagExist(tiddler, "private"))
      {
          score=score+12;
      }
      score += getEventScore(personname);
      const etiddler = $tw.wiki.getTiddler(personname);
	   if (etiddler != null) {
	   	const assertiontiddlers = $tw.wiki.filterTiddlers("[tag[assertion]contains:people[" + etiddler.fields.title + "]]", null, '');
		   $tw.utils.each(assertiontiddlers, (sourcetitle) => {
		      score += getAssertionScore(sourcetitle);
		   });
	   }
       const assertiontiddlers = $tw.wiki.filterTiddlers("[tag[assertion]contains:people[" + tiddler.fields.title + "]]", null, '');
	   $tw.utils.each(assertiontiddlers, (sourcetitle) => {
			  score += getAssertionScore(sourcetitle);
		});
    }
    if (score > 3) {
       score=score/4;
    }
    if (score > 100) score=100;
    return score.toFixed(0);
  };
}());

/*\
title: RelationshipCalc.js
type: application/javascript
module-type: macro

Macro to return informal name format of a name

\*/

(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "RelationshipCalc";

exports.params = [
{ name: "person1name" }, {name: "person2name"}, {name: "short"}
];

function personExists(person) {
   var tiddler = $tw.wiki.getTiddler(person);
   if (tiddler != null) {
      return true;
   }
   return false;
}

function getCaption(person) {
   var tiddler = $tw.wiki.getTiddler(person);
   if (tiddler != null) {
      var captionValue = tiddler.getFieldString("caption");
      if (captionValue != "") {
         return captionValue;
      }
   }
   return person;
}

function getAncestor(person, generation, personlist) {
   if (generation > 30) {
      return personlistx;
   }
   var tiddler = $tw.wiki.getTiddler(person);
   var personlistx = personlist;
   if (tiddler != null) {
      var fatherValue = $tw.utils.parseStringArray(tiddler.getFieldString("father") || "");
		if (fatherValue.length > 0) {
         //fatherValue = fatherValue.replace("/[/g", "");
         //fatherValue = fatherValue.replace("/]/g", "");
			personlistx = personlistx + fatherValue[0] + ";" + generation.toString() + ";";
			personlistx = getAncestor(fatherValue[0], generation+1, personlistx);
		}
		var motherValue = $tw.utils.parseStringArray(tiddler.getFieldString("mother") || "");
		if (motherValue.length > 0) {
         //motherValue = motherValue.replace("/[/g", "");
         //motherValue = motherValue.replace("/]/g", "");
			personlistx = personlistx + motherValue[0] + ";" + generation.toString() + ";";
			personlistx = getAncestor(motherValue[0], generation+1, personlistx);
		}
	}
	return personlistx;
}

/*
Run the macro
*/
exports.run = function(person1name, person2name, short) {

var p1 = $tw.utils.parseStringArray(person1name || "");
var p2 = $tw.utils.parseStringArray(person2name || "");

var per1 = p1[0];
var per2 = p2[0];


if (!personExists(per1)) {
   return "one or both individuals do not exist.";
}
if (!personExists(per2)) {
   return "one or both individuals do not exist.";
}

var caption1 = getCaption(per1);
var caption2 = getCaption(per2);

if (per1 == per2) {
    if (short == "short") {
       return "self";
    }
    return "Relationship of " + caption1 + " and " + caption2 + " is Self";
}

var list1 = getAncestor(per1, 1, "");
var list2 = getAncestor(per2, 1, "");

//return list2;

const person1list = list1.split(';');
const person2list = list2.split(';');

//return person2list.length;

for(var j = 0; j < person1list.length; j=j+2) {
  if (per2 == person1list[j]) {
	var generation1 = person1list[j+1];
	var generation2 = 0;
	if (generation1 == "1") {
      if (short == "short") {
         return "child";
      }
		return caption1 + " is a child of " + caption2 + " [parent].  In general, 50% of their genes are common.";
	}
	if (generation1 == "2") {
      if (short == "short") {
         return "grand child";
      }
		return caption1 + " is a grand child of " + caption2 + " [grand parent].  In general, 25% of their genes are common.";
	}
   try {
      var igeneration = parseInt(generation1 - 2);
      var comgenes = 50 * (.5 ** (generation1 - 1))
      if (short == "short") {
         return igeneration.toString() + "X great grand child";
      }
      return caption1 + " is a " + igeneration.toString() + "X great grand child of " + caption2 + " [" + igeneration.toString() + "X great grand parent].  In general, " + comgenes + "% of their genes are common.";
   }
   catch(err) {
      return "ERROR: bad generation number: " + generation1;
   }
  }
}

for(var i = 0; i < person2list.length; i=i+2) {
  if (per1 == person2list[i]) {
	var generation2 = person2list[i+1];
	var generation1 = 0;
	if (generation2 == "1") {
      if (short == "short") {
         return "parent";
      }
		return caption1 + " is a parent of " + caption2 + " [child].  In general, 50% of their genes are common.";
	}
	if (generation2 == "2") {
      if (short == "short") {
         return "grand parent";
      }
		return caption1 + " is a grand parent of " + caption2 + " [grand child].  In general, 25% of their genes are common.";
	}
   try {
      var igeneration = parseInt(generation2 - 2);
      var comgenes = 50 * (.5 ** (generation2 - 1));
      if (short == "short") {
         return igeneration.toString() + "X great grand parent";
      }
      return caption1 + " is a " + igeneration.toString() + "X great grand parent of " + caption2 + " [" + igeneration.toString() + "X great grand child].  In general, " + comgenes + "% of their genes are common.";
   }
   catch(err) {
      return "ERROR: bad generation number: " + generation1;
   }
  }
}





for(var i = 0; i < person2list.length; i=i+2) {
   for(var j = 0; j < person1list.length; j=j+2) {
		if (person2list[i] == person1list[j] && person1list[j] != "") {
		   var generation1 = person1list[j+1];
		   var generation2 = person2list[i+1];
         var commonAncestor = getCaption(person1list[j]);
		   if (generation1 == "1" && generation2 == "1") {
           if (short == "short") {
              return "sibling";
           }
			  return caption1 + " is a sibling of " + caption2 + ".  In general, 50% of their genes are common.";
		   }

         var level1 = parseInt(generation1);
         var level2 = parseInt(generation2);
         if (level1 < level2) {
             if (level1 == 1) {
                 var ilevel = level2 - level1 - 1;
                 var comgenes = 25 * (.5 ** ilevel);
                 if (short == "short") {
                   if (ilevel == 0) {
                       return "Uncle/Aunt";
                   }
                   return ilevel.toString() + " X Great Uncle/Aunt";
                 }
                 return caption1 + " is an Uncle/Aunt of " + caption2 + " [Nephew/Niece], # of greats: " + ilevel.toString() +  ".  In general, " + comgenes + "% of their genes are common.";
             }
         }

         if (level2 == 1) {
            var ilevel = level1 - level2 - 1;
            var comgenes = 25 * (.5 ** ilevel);
            if (short == "short") {
               if (ilevel == 0) {
                   return "Nephew/Niece";
               }
               return ilevel.toString() + " X Great Nephew/Niece";
            }
            return caption1 + " is a Nephew/Niece of " + caption2 + " [Uncle/Aunt], # of greats: " + ilevel.toString() +  ".  In general, " + comgenes + "% of their genes are common.";
         }

		   var icousin = level2 - 1;
         var igen = Math.abs(level1 - level2);
         var ext = "";

         switch(icousin) {
            case 1:
                ext = "st";
                break;
            case 2:
                ext = "nd";
                break;
            case 3:
                ext = "rd";
                break;
            default:
                ext = "th";
         }

         var comgenes = 25 * (.5 ** icousin) * (.5 ** igen);

         if (igen == 0 ) {
             if (short == "short") {
               return icousin.toString() + ext + " cousins";
             }
             return caption1 + " and " + caption2 + " are " + icousin.toString() + ext + " cousins.  Common ancestor is: " + commonAncestor + ".  In general, " + comgenes.toString() + "% of their genes are common.";
         } else {
             if (short == "short") {
               return icousin.toString() + ext + " cousins, " + igen.toString() + " generation(s) removed";
             }
             return caption1 + " and " + caption2 + " are " + icousin.toString() + ext + " cousins, " + igen.toString() + " generation(s) removed.  Common ancestor is: " + commonAncestor + ".  In general, " + comgenes.toString() + "% of their genes are common.";
         }
		}
   }
}

return "no blood relationship found";

};

})();

