/*\
title: getUrlFilename.js
type: application/javascript
module-type: macro

Macro to return a Url filename

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "getUrlFilename";

exports.params = [
{ name: "theParameter" }, { name: "defaultValue"}
];

/*
Run the macro
*/
exports.run = function(theParameter, defaultValue) {

var url = window.location.pathname;
var filename = url.substring(url.lastIndexOf('/')+1);
return filename;

};

})();

/*\
title: getUrlNewFilename.js
type: application/javascript
module-type: macro

Macro to return a Url filename

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "getUrlNewFilename";

exports.params = [
{ name: "theFormat" }
];

/*
Run the macro
*/
exports.run = function(theFormat) {

var url = window.location.pathname;
/* var filename = url.substring(url.lastIndexOf('/')+1); */
var filename = url.match(/([^\/]+)(?=\.\w+$)/)[0];
var matches = filename.match(/(\d+)/g);
var number = 1;
if (matches) {
 number = Number(matches[matches.length-1]);
 number++;
}
var num = number.toString();
while (num.length < 4) num = "0" + num;
return theFormat + num + ".html";
};

})();

