    else {
        days = DOD.getDate() - DOB.getDate();
        months = DOD.getMonth() - DOB.getMonth();
        if (months < 0 ) {
            months = months + 12;
        }
        area=" simple";
    }
    var age = years.toString();
    if (years.length == 0) {
        age = "0";
    }
    return age;
  } else {
    return "0";
  }
return "0";
