/*\
title: getName.js
type: application/javascript
module-type: macro

Macro to return informal name format of a name

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "getName";

exports.params = [
{ name: "caption" }, {name: "title"}
];

/*
Run the macro
*/
exports.run = function(caption, title) {

if (caption.length > 0) {
  var n = caption.indexOf(" (");
  if (n == -1) {
    return caption;
  }
  return caption.substr(0, n);
} else {
    return title;
}

};

})();

/* \
title: getPersonScore.js
type: application/javascript
module-type: macro

Macro to return informal name format of a name

\ */

(function () {
/* jslint node: true, browser: true */
  /* global $tw: false */

  /*
Information about this macro
*/

  exports.name = 'getPersonScore';

  exports.params = [
    { name: 'personname' },
  ];

  function personExists(person) {
    const tiddler = $tw.wiki.getTiddler(person);
    if (tiddler != null) {
      return true;
