  function getAssertionScore(tiddlerTitle) {
    const tiddler = $tw.wiki.getTiddler(tiddlerTitle);
    var score = 0;
    if (tiddler != null) {
      if (tiddler.fields["proof"] !== null) {
         if (tiddler.fields["proof"] == "argument") score=score+1;
