              if (evidencetiddler.fields["strength"] == "strong") score=score+2;
              if (evidencetiddler.fields["strength"] == "moderate") score=score+1;
              if (evidencetiddler.fields["strength"] == "weak") score=score-1;
           }
           if (evidencetiddler.fields["infovalue"] !== null) {
              if (evidencetiddler.fields["infovalue"] == "none") score=score-1;
              if (evidencetiddler.fields["infovalue"] == "incomplete") score=score-1;
