Information about this macro
*/

exports.name = "RelationshipCalc";

exports.params = [
{ name: "person1name" }, {name: "person2name"}, {name: "short"}
];

function personExists(person) {
   var tiddler = $tw.wiki.getTiddler(person);
   if (tiddler != null) {
      return true;
   }
   return false;
}

function getCaption(person) {
   var tiddler = $tw.wiki.getTiddler(person);
   if (tiddler != null) {
      var captionValue = tiddler.getFieldString("caption");
      if (captionValue != "") {
         return captionValue;
      }
   }
   return person;
}

function getAncestor(person, generation, personlist) {
   if (generation > 30) {
      return personlistx;
   }
   var tiddler = $tw.wiki.getTiddler(person);
   var personlistx = personlist;
   if (tiddler != null) {
      var fatherValue = $tw.utils.parseStringArray(tiddler.getFieldString("father") || "");
		if (fatherValue.length > 0) {
         //fatherValue = fatherValue.replace("/[/g", "");
         //fatherValue = fatherValue.replace("/]/g", "");
			personlistx = personlistx + fatherValue[0] + ";" + generation.toString() + ";";
			personlistx = getAncestor(fatherValue[0], generation+1, personlistx);
		}
		var motherValue = $tw.utils.parseStringArray(tiddler.getFieldString("mother") || "");
		if (motherValue.length > 0) {
         //motherValue = motherValue.replace("/[/g", "");
         //motherValue = motherValue.replace("/]/g", "");
			personlistx = personlistx + motherValue[0] + ";" + generation.toString() + ";";
			personlistx = getAncestor(motherValue[0], generation+1, personlistx);
		}
	}
	return personlistx;
}

/*
Run the macro
*/
exports.run = function(person1name, person2name, short) {

var p1 = $tw.utils.parseStringArray(person1name || "");
var p2 = $tw.utils.parseStringArray(person2name || "");

var per1 = p1[0];
var per2 = p2[0];


if (!personExists(per1)) {
   return "one or both individuals do not exist.";
}
if (!personExists(per2)) {
   return "one or both individuals do not exist.";
}

var caption1 = getCaption(per1);
var caption2 = getCaption(per2);

if (per1 == per2) {
    if (short == "short") {
       return "self";
    }
    return "Relationship of " + caption1 + " and " + caption2 + " is Self";
}

var list1 = getAncestor(per1, 1, "");
var list2 = getAncestor(per2, 1, "");

//return list2;

const person1list = list1.split(';');
const person2list = list2.split(';');

//return person2list.length;

for(var j = 0; j < person1list.length; j=j+2) {
  if (per2 == person1list[j]) {
	var generation1 = person1list[j+1];
	var generation2 = 0;
	if (generation1 == "1") {
      if (short == "short") {
         return "child";
      }
		return caption1 + " is a child of " + caption2 + " [parent].  In general, 50% of their genes are common.";
	}
	if (generation1 == "2") {
      if (short == "short") {
         return "grand child";
      }
		return caption1 + " is a grand child of " + caption2 + " [grand parent].  In general, 25% of their genes are common.";
	}
   try {
      var igeneration = parseInt(generation1 - 2);
      var comgenes = 50 * (.5 ** (generation1 - 1))
      if (short == "short") {
         return igeneration.toString() + "X great grand child";
      }
      return caption1 + " is a " + igeneration.toString() + "X great grand child of " + caption2 + " [" + igeneration.toString() + "X great grand parent].  In general, " + comgenes + "% of their genes are common.";
   }
   catch(err) {
      return "ERROR: bad generation number: " + generation1;
   }
  }
}

for(var i = 0; i < person2list.length; i=i+2) {
  if (per1 == person2list[i]) {
	var generation2 = person2list[i+1];
	var generation1 = 0;
	if (generation2 == "1") {
      if (short == "short") {
         return "parent";
      }
		return caption1 + " is a parent of " + caption2 + " [child].  In general, 50% of their genes are common.";
	}
	if (generation2 == "2") {
      if (short == "short") {
         return "grand parent";
      }
		return caption1 + " is a grand parent of " + caption2 + " [grand child].  In general, 25% of their genes are common.";
	}
   try {
      var igeneration = parseInt(generation2 - 2);
      var comgenes = 50 * (.5 ** (generation2 - 1));
      if (short == "short") {
         return igeneration.toString() + "X great grand parent";
      }
      return caption1 + " is a " + igeneration.toString() + "X great grand parent of " + caption2 + " [" + igeneration.toString() + "X great grand child].  In general, " + comgenes + "% of their genes are common.";
   }
   catch(err) {
      return "ERROR: bad generation number: " + generation1;
   }
  }
}





for(var i = 0; i < person2list.length; i=i+2) {
   for(var j = 0; j < person1list.length; j=j+2) {
		if (person2list[i] == person1list[j] && person1list[j] != "") {
		   var generation1 = person1list[j+1];
		   var generation2 = person2list[i+1];
         var commonAncestor = getCaption(person1list[j]);
		   if (generation1 == "1" && generation2 == "1") {
           if (short == "short") {
              return "sibling";
           }
			  return caption1 + " is a sibling of " + caption2 + ".  In general, 50% of their genes are common.";
		   }

         var level1 = parseInt(generation1);
         var level2 = parseInt(generation2);
         if (level1 < level2) {
             if (level1 == 1) {
                 var ilevel = level2 - level1 - 1;
                 var comgenes = 25 * (.5 ** ilevel);
                 if (short == "short") {
                   if (ilevel == 0) {
                       return "Uncle/Aunt";
                   }
                   return ilevel.toString() + " X Great Uncle/Aunt";
                 }
                 return caption1 + " is an Uncle/Aunt of " + caption2 + " [Nephew/Niece], # of greats: " + ilevel.toString() +  ".  In general, " + comgenes + "% of their genes are common.";
             }
         }

         if (level2 == 1) {
            var ilevel = level1 - level2 - 1;
            var comgenes = 25 * (.5 ** ilevel);
            if (short == "short") {
               if (ilevel == 0) {
                   return "Nephew/Niece";
               }
               return ilevel.toString() + " X Great Nephew/Niece";
            }
            return caption1 + " is a Nephew/Niece of " + caption2 + " [Uncle/Aunt], # of greats: " + ilevel.toString() +  ".  In general, " + comgenes + "% of their genes are common.";
         }
         var icousin = level2;
         if (level1 >= level2) {
		       icousin = level2 - 1;
         }
         if (level1 < level2) {
             icousin = level1 - 1;
         }
         var igen = Math.abs(level1 - level2);
         var ext = "";

         switch(icousin) {
            case 1:
                ext = "st";
                break;
            case 2:
                ext = "nd";
                break;
            case 3:
                ext = "rd";
                break;
            default:
                ext = "th";
         }

         var comgenes = 25 * (.5 ** icousin) * (.5 ** igen);

         if (igen == 0 ) {
             if (short == "short") {
               return icousin.toString() + ext + " cousins";
             }
             return caption1 + " and " + caption2 + " are " + icousin.toString() + ext + " cousins.  Common ancestor is: " + commonAncestor + ".  In general, " + comgenes.toString() + "% of their genes are common.";
         } else {
             if (short == "short") {
               return icousin.toString() + ext + " cousins, " + igen.toString() + " generation(s) removed";
             }
             return caption1 + " and " + caption2 + " are " + icousin.toString() + ext + " cousins, " + igen.toString() + " generation(s) removed.  Common ancestor is: " + commonAncestor + ".  In general, " + comgenes.toString() + "% of their genes are common.";
         }
		}
   }
}

return "no blood relationship found";

};

})();

