type: application/javascript
module-type: filteroperator

Filter operator for sorting

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Export our filter function
*/
exports.rsort = function(source,operator,options) {
	var results = prepare_results(source);
	options.wiki.sortTiddlers(results,"title",operator.prefix === "!",false,false);
   if (operator.operand.length > 0) {
      var reresults = reorder_results(results, operator.operand);
	   return reresults;
   } else {
      return results;
   }
};

var prepare_results = function (source) {
	var results = [];
	source(function(tiddler,title) {
		results.push(title);
	});
	return results;
};

var reorder_results = function (source, operand) {
   const operandArr = operand.split(" ");
	var results = [];
   for (let i=0; i<250; i++) {
      for (let j=0; j<source.length; j++) {
         if (i < source[j].length) {
           for (let k=0; k < operandArr.length; k++) {
              if (!results.includes(source[j]) && operandArr[k].length > 0 ) {
                 var pos=source[j].toUpperCase().indexOf(operandArr[k].toUpperCase());
                 if (pos==i) {
                   results.push(source[j]);
                   break;
                }
              }
           }
         }
 	   }
  }

  return results;
};


})();

var RevealWidget = require("$:/core/modules/widgets/reveal.js").reveal;

RevealWidget.prototype.refresh = function(changedTiddlers) {
	var changedAttributes = this.computeAttributes();
	if(changedAttributes.state || changedAttributes.type || changedAttributes.text || changedAttributes.position || changedAttributes.positionAllowNegative || changedAttributes["default"] || changedAttributes.animate || changedAttributes.stateTitle || changedAttributes.stateField || changedAttributes.stateIndex || changedAttributes.style) {
		this.refreshSelf();
		return true;
	} else {
		var currentlyOpen = this.isOpen;
		this.readState();
		if(this.isOpen !== currentlyOpen) {
			if(this.retain === "yes") {
				this.updateState();
			} else {
				this.refreshSelf();
				return true;
			}
		} else if(changedTiddlers[this.state] || changedTiddlers[this.stateTitle]) {
if(this.type == "popup")
			this.positionPopup(this.domNodes[0]);
		}

		return this.refreshChildren(changedTiddlers);
	}
}


var sq_fm_orig_positionPopup = RevealWidget.prototype.positionPopup;

RevealWidget.prototype.positionPopup = function(domNode) {
	if (this.position == "aboveleft" || this.position == "belowright") {
		domNode.style.position = "absolute";
		domNode.style.zIndex = "1000";
		var left,top;
		switch(this.position) {
			case "aboveleft":
				left = this.popup.left - domNode.offsetWidth;
				top = this.popup.top - domNode.offsetHeight;
				break;
			case "belowright":
				left = this.popup.left + this.popup.width;
				top = this.popup.top + this.popup.height;
				break;
		}
		if(!this.positionAllowNegative) {
			left = Math.max(0,left);
			top = Math.max(0,top);
		}
		domNode.style.left = left + "px";
		domNode.style.top = top + "px";		
	} else {
		sq_fm_orig_positionPopup.apply(this,arguments);
	}
};

