(function(){"use strict";exports.name="chapternotes";exports.params=[{name:"chapter"}];exports.run=function(chapter){var markers=[];var textbody="";var returnString="";var tiddler=$tw.wiki.getTiddler(chapter);if(tiddler!=null){textbody=tiddler.getFieldString("text");if(textbody==""){return"Error (4) - Text body of chapter tiddler ( "+chapter+" ) found empty."}}else{return"Error (3) - Chapter tiddler ( "+chapter+" ) not found."}
var startpos=0;var max=0;var position=1;var positionend=-1;while(position>0&&max<200){var thissearchstring=textbody.substring(startpos);position=thissearchstring.search("<<clstip ");if(position>-1){positionend=thissearchstring.search(">>");if(positionend==-1){return"Error (1) in usage of clstip near position: "+position;}
var clsstring=thissearchstring.substring(position,positionend+2)
const cls=clsstring.split("\"");if(cls.length<8){return"Error (2) in usage of clstip near position: "+position+" element count: "+cls.length;}
var bfound=false;for(let i=0;i<markers.length;i++){if(cls[3]==markers[i]){bfound=true;break;}}
if(!bfound){markers.push(cls[3]);returnString=returnString+"<div class=\"cit\"><a id=\"#"+cls[3]+"\">"+cls[3]+"</a> {{"+cls[1]+cls[7]+"}}</div><br/>";startpos=startpos+positionend+2;}
max++;}}
return returnString;};})();

