        var tiddler = $tw.wiki.getTiddler(person);
        if (tiddler != null) {
            var captionValue = tiddler.getFieldString("caption");
            if (captionValue != "") {
                return captionValue;
            }
        }
        return person;
    }
    function getGender(person) {
        var tiddler = $tw.wiki.getTiddler(person);
        if (tiddler != null) {
            var genderValue = tiddler.getFieldString("tags");
            if (genderValue.includes("female")) {
                return "female";
            } else {
                return "male";
            }
        }
        return "";
    }
    function getAncestor(person, generation, personlist) {
        if (generation > 30) {
            return personlistx;
        }
