RevealWidget.prototype.refresh = function(changedTiddlers) {
	var changedAttributes = this.computeAttributes();
	if(changedAttributes.state || changedAttributes.type || changedAttributes.text || changedAttributes.position || changedAttributes.positionAllowNegative || changedAttributes["default"] || changedAttributes.animate || changedAttributes.stateTitle || changedAttributes.stateField || changedAttributes.stateIndex || changedAttributes.style) {
		this.refreshSelf();
		return true;
	} else {
		var currentlyOpen = this.isOpen;
		this.readState();
		if(this.isOpen !== currentlyOpen) {
			if(this.retain === "yes") {
				this.updateState();
			} else {
				this.refreshSelf();
				return true;
			}
		} else if(changedTiddlers[this.state] || changedTiddlers[this.stateTitle]) {
if(this.type == "popup")
			this.positionPopup(this.domNodes[0]);
		}

		return this.refreshChildren(changedTiddlers);
	}
}


var sq_fm_orig_positionPopup = RevealWidget.prototype.positionPopup;

RevealWidget.prototype.positionPopup = function(domNode) {
	if (this.position == "aboveleft" || this.position == "belowright") {
		domNode.style.position = "absolute";
		domNode.style.zIndex = "1000";
		var left,top;
		switch(this.position) {
			case "aboveleft":
				left = this.popup.left - domNode.offsetWidth;
				top = this.popup.top - domNode.offsetHeight;
				break;
			case "belowright":
				left = this.popup.left + this.popup.width;
				top = this.popup.top + this.popup.height;
				break;
		}
		if(!this.positionAllowNegative) {
			left = Math.max(0,left);
			top = Math.max(0,top);
		}
		domNode.style.left = left + "px";
		domNode.style.top = top + "px";		
	} else {
		sq_fm_orig_positionPopup.apply(this,arguments);
	}
};

